mapper.select:
select j.* , (select count(*) from comment where j_idx=j.j_idx) c_count from ( select * from joonggo
j_title like concat('%' , #{ title }, '%')
or m_nick like concat('%' , #{ nick } , '%')
or j_content like concat('%' , #{ content } , '%')
)j limit #{start}, #{ end }

mapper.update:
update joonggo set                                                                                              
j_sell_yn = 'y'
where j_idx = #{j_idx}
